import { useEffect, useState } from "react";
import { Helmet } from "react-helmet";
import { useNavigate, useParams } from "react-router-dom";
import LoadingBar from "react-top-loading-bar";
import Navbar from "../Navbar";
import Side from "../Side";
import Sidebar from "../Sidebar";
import Channel from "./Channel";
import { NavLink } from "react-router-dom";
import LazyImage from "../../pages/LazyImage";
import thumbnail from '../../assets/placeholder.jpg';

export default function ChannelPlaylist({ userInfo, subdata, firstplaylist, playlists, playlistvideos }){
    const [isLoading, setIsLoading] = useState(true);
    const navigate = useNavigate();
    const [userinfo, setuserinfo] = useState([]);
    setPlaylists(data.user['playlist_data']);
    setPlaylistVideos(data.user['playlist_data'][0]['playlistvideos']);
    
    const { channelslug } = useParams();
    const channelslu = channelslug.replace('@', '');

    useEffect(() => {
        fetch(`http://localhost:8000/api/user_channel_json/${channelslu}`, {
            method: 'GET',
            credentials: 'include',
        })
        .then(response => {
            if (response.status === 200) {
                return response.json();
            } else {
                navigate('/');
                throw new Error('Failed to fetch user info');
            }
        })
        .then(data => {
            setuserinfo(data);
            setIsLoading(false);
            console.log(data);
        })
        .catch(error => {
            console.error('Error fetching user info:', error);
        });
    }, [])


    return (
        <>
            <Helmet>
                {userinfo.name ? (
                    <title>{`${userinfo.name} - YouTube`}</title>
                ):(
                    <title>YouTube</title>
                )}
            </Helmet>
            <LoadingBar
                color="#ff0000" // Customize the color (e.g., red)
                height={1.5}       // Customize the height (4 pixels)
                progress={isLoading ? 30 : 100} // Set progress based on loading state
            />
            <Navbar userInfo={userInfo} />
            <div className="main">
                <Side />
                <Sidebar subdata={subdata} />
                <div className="main-scroll">
                    <div className="channellayer">
                        <Channel channelslug={channelslu} userinfo={userinfo} />
                        <div className="channelvideoscon">
                            {playlists ? (
                                <>
                                    <h4>Created Playlists ({playlists.length})</h4>
                                    <div className="channelvideoscon-inner">
                                        {playlists.length <= 4 ? (
                                            <>
                                                {playlists.map((video, index) => (
                                                    <div className="channelvid" key={video.id}>
                                                        <div className="channelvid-img">
                                                            <span>
                                                                {playlists[index].playlistvideos.length} {playlists[index].playlistvideos.length === 1 ? "video":"videos"}
                                                            </span>
                                                            <NavLink to={`/search?=${video.playlistname}`}>
                                                                <LazyImage src={thumbnail} data-real-src={thumbnail} alt={playlists[index]}/>
                                                            </NavLink>
                                                        </div>
                                                        <div className="channelvid-detail">
                                                            <div className="meta">
                                                                <h3>{video.playlistname}</h3>
                                                                
                                                                <div className="metablock">
                                                                    <NavLink to='/' style={{textTransform: 'uppercase', marginTop: '5px'}}>View full playlist</NavLink>
                                                                </div>
                                                            </div>
                                                            <div className="menu">
                                                                <button>
                                                                    <svg height="24" viewBox="0 0 24 24" width="24" focusable="false"><path d="M12 16.5c.83 0 1.5.67 1.5 1.5s-.67 1.5-1.5 1.5-1.5-.67-1.5-1.5.67-1.5 1.5-1.5zM10.5 12c0 .83.67 1.5 1.5 1.5s1.5-.67 1.5-1.5-.67-1.5-1.5-1.5-1.5.67-1.5 1.5zm0-6c0 .83.67 1.5 1.5 1.5s1.5-.67 1.5-1.5-.67-1.5-1.5-1.5-1.5.67-1.5 1.5z"></path></svg>
                                                                </button>
                                                            </div>
                                                        </div>
                                                    </div>
                                                ))}
                                                <div className="channelvid"></div>
                                                <div className="channelvid"></div>
                                                <div className="channelvid"></div>
                                                <div className="channelvid"></div>
                                                <div className="channelvid"></div>
                                                <div className="channelvid"></div>
                                            </>
                                        ):(
                                            <>
                                                {playlists.map((video, index) => (
                                                    <div className="channelvid" key={video.id}>
                                                        <div className="channelvid-img">
                                                            <span>{playlists[index].playlistvideos.length} {playlists[index].playlistvideos.length === 1 ? "video":"videos"}</span>
                                                            <NavLink to={`/search?=${video.playlistname}`}>
                                                                <LazyImage src={thumbnail} data-real-src={thumbnail} alt={playlists[index].id}/>
                                                            </NavLink>
                                                        </div>
                                                        <div className="channelvid-detail">
                                                            <div className="meta">
                                                                <h3>{video.playlistname}</h3>
                                                                <label>{video.playlistvisibility}</label>
                                                                <div className="metablock">
                                                                    <NavLink to='/' style={{textTransform: '', marginTop: '5px', fontWeight: '500', color: '#606060'}}>View full playlist</NavLink>
                                                                </div>
                                                            </div>
                                                            <div className="menu">
                                                                <button>
                                                                    <svg height="24" viewBox="0 0 24 24" width="24" focusable="false"><path d="M12 16.5c.83 0 1.5.67 1.5 1.5s-.67 1.5-1.5 1.5-1.5-.67-1.5-1.5.67-1.5 1.5-1.5zM10.5 12c0 .83.67 1.5 1.5 1.5s1.5-.67 1.5-1.5-.67-1.5-1.5-1.5-1.5.67-1.5 1.5zm0-6c0 .83.67 1.5 1.5 1.5s1.5-.67 1.5-1.5-.67-1.5-1.5-1.5-1.5.67-1.5 1.5z"></path></svg>
                                                                </button>
                                                            </div>
                                                        </div>
                                                    </div>
                                                ))}
                                            </>
                                        )}
                                    </div>
                                </>
                            ):(
                                <label>No Playlists</label>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}
