import { useState, useEffect } from "react";
import thumbnail from '../../assets/placeholder.jpg'
import { useParams } from "react-router-dom";
import { Helmet } from "react-helmet";
import { NavLink } from "react-router-dom";
import ChannelLayer from "./ChannelLayer";
import ChannelButtons from "./ChannelButtons";

function ChannelHome(){
    const [delayedLoading, setDelayedLoading] = useState(false);
    const [title, setTitle] = useState([]);
    const [userInfo, setUserInfo] = useState([]);
    const isChannelHome = location.pathname === `/channel/${channelId}`;
    const isChannelVideos = location.pathname === `/channel/${channelId}/videos`;
    const isChannelAbout = location.pathname === `/channel/${channelId}/about`;
    const { channelId } = useParams();

    useEffect(() => {


        const apiurl = 'http://localhost:8000/api/get_user_data';

        fetch(apiurl, {
            method: 'GET',
            credentials: 'include',
        })
        .then(response => {
            if (response.status === 200) {
                return response.json();
            } else {
                throw new Error('Failed to fetch user info');
            }
        })
        .then(data => {
            setTotalview(data.total_views);
            setUserInfo(data.user);
        })
        .catch(error => {
            console.error('Error fetching user info:', error);
        });
        
        const videos_data = () => {
            fetch('http://localhost:8000/api/user_videos', {
                method: 'GET',
                credentials: 'include',
            })
            .then(response => {
                if(response.status == 200){
                    return response.json();
                } else {
                    throw new Error('failed to fetch user videos..');
                }
            })
            .then(data => {
                setTitle(data.channeluser);
                // setIsLoading(false);
                setTimeout(() => {
                    setDelayedLoading(true);
                }, 100);
            })
            .catch(error => {
                console.error('Error fetching user info:', error);
            });
        }
        videos_data();


        


    }, []);



    return (
        <>
            <Helmet>
                <title>{`${title} - YouTube`}</title>
            </Helmet>

            <Navbar />
            <div className="main">
                <Side />
                <Sidebar />
                <div className="main-scroll">
                    <div className="channelcon">
                        <div className="banner" style={{}}>
                            
                        </div>
                        <div className="channel-outer">
                            <ChannelLayer />
                            <ChannelButtons />


                            <div className="channel-inner">
                                <div className="featured">
                                    <NavLink to='/'>
                                        <div className="f-inner">
                                            <div onClick={() => {'/'}}>
                                                <div className="feature-img">
                                                    <video src="" autoPlay loop muted controls></video>
                                                </div>
                                            </div>
                                            <div className="feature-detail">
                                                <div onClick={() => {'/'}}>
                                                    <h4>title</h4>
                                                </div>
                                                <div onClick={() => {'/'}}>author</div>
                                                <span>
                                                    <span>23 views</span> • <span>2 days ago</span>
                                                </span>  
                                            </div>
                                        </div>
                                    </NavLink>
                                </div>

                                <div className="posts">
                                    <p>Videos (0)</p>
                                    <div className="post-inner">
                                        <div className="post">
                                            <NavLink to='/'>
                                                <div className="post-img">
                                                    <img src={thumbnail} data-real-src={thumbnail} />   
                                                    <label>00:08</label>      
                                                </div>
                                            </NavLink>
                                        <div className="post-detail">
                                                <NavLink>
                                                    <h3>title</h3>
                                                </NavLink>
                                                <NavLink to='/'>marvel</NavLink>
                                                <span>
                                                    <span>34 views</span> • <span>2 days ago</span>
                                                </span>
                                            </div>   
                                        </div>  
                                        <div className="post"></div>  
                                        <div className="post"></div>  
                                        <div className="post"></div>  
                                        <div className="post"></div>  
                                        <div className="post"></div>  
                                        <div className="post"></div>  
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}
export default ChannelHome;
