import React, { useEffect, useRef } from 'react';
import '../../src/player.css';
import '../../src/player.js';

function VideoPlayer({ url }) {
  const videoRef = useRef(null);

  const togglePlay = () => {
    const video = videoRef.current;
    video.paused ? video.play() : video.pause();
  }

  const toggleFullScreenMode = () => {
    const container = document.querySelector('.video-contain');
    if (!document.fullscreenElement) container.requestFullscreen();
    else document.exitFullscreen();
  }

  const toggleMiniPlayerMode = () => {
    const video = videoRef.current;
    if (video?.classList.contains('mini-player')) document.exitPictureInPicture();
    else video?.requestPictureInPicture();
  }

  useEffect(() => {
    const video = videoRef.current;

    const handleKeyDown = (e) => {
      if (e.target.tagName.toLowerCase() === 'input') return;
      switch (e.key.toLowerCase()) {
        case " ":
          togglePlay();
          break;
        case 'arrowleft':
          video.currentTime -= 5;
          break;
        case 'arrowright':
          video.currentTime += 5;
          break;
        default:
          break;
      }
    };
    document.addEventListener('keydown', handleKeyDown);

    return () => {
      document.removeEventListener('keydown', handleKeyDown);
    }
  }, []);

  return (
    <div className="video-contain paused" data-volume-level="high">
      <div className="video-controls-container">
        <div className="controls">
          <button className="play-pause-btn" onClick={togglePlay}>
            <svg className="play-icon" viewBox="0 0 24 24">
              <path fill="currentColor" d="M8,5.14V19.14L19,12.14L8,5.14Z" />
            </svg>
            <svg className="pause-icon" viewBox="0 0 24 24">
              <path fill="currentColor" d="M14,19H18V5H14M6,19H10V5H6V19Z" />
            </svg>
          </button>
          <button className="mini-player-btn" onClick={toggleMiniPlayerMode}>
            <svg viewBox="0 0 24 24">
              <path fill="currentColor" d="M21 3H3c-1.1 0-2 .9-2 2v14c0 1.1.9 2 2 2h18c1.1 0 2-.9 2-2V5c0-1.1-.9-2-2-2zm0 16H3V5h18v14zm-10-7h9v6h-9z"/>
            </svg>
          </button>
          <button className="full-screen-btn" onClick={toggleFullScreenMode}>
            <svg className="open" viewBox="0 0 24 24">
              <path fill="currentColor" d="M7 14H5v5h5v-2H7v-3zm-2-4h2V7h3V5H5v5zm12 7h-3v2h5v-5h-2v3zM14 5v2h3v3h2V5h-5z"/>
            </svg>
            <svg className="close" viewBox="0 0 24 24">
              <path fill="currentColor" d="M5 16h3v3h2v-5H5v2zm3-8H5v2h5V5H8v3zm6 11h2v-3h3v-2h-5v5zm2-11V5h-2v5h5V8h-3z"/>
            </svg>
          </button>
        </div>
      </div>
      <video src={url} ref={videoRef} />
    </div>
  );
}

export default VideoPlayer;
