import { useEffect, useState } from "react";
import LoadingBar from "react-top-loading-bar";


function AuthorChannelHome({ userInfo, authorinfo, authorslug }){
    const [isLoading, setIsLoading] = useState(true);


    useEffect(() => {
        fetch('http://localhost:8000/api')
        .then(response => {
            return response
        })
        .then(res => {
            console.log(res);
        })
    }, [])
    return (
        <>
            <LoadingBar
                color="#ff0000" // Customize the color (e.g., red)
                height={1.5}       // Customize the height (4 pixels)
                progress={isLoading ? 30 : 100} // Set progress based on loading state
            />
            {authorinfo.channelname}
        </>
    )
}
export default AuthorChannelHome;
