import { Helmet } from "react-helmet";
import Navbar from "../components/Navbar";
import Side from "../components/Side";
import Sidebar from "../components/Sidebar";
import { useEffect, useState } from "react";
import LoadingBar from 'react-top-loading-bar';
import NoInternetPage from "./NoInternetPage";

function Library({ userInfo }){
    // const [videos, setVideos] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    // const [delayedLoading, setDelayedLoading] = useState(false);
    const [isOnline, setIsOnline] = useState(navigator.onLine);

    useEffect(() => {

        const fetchData = () => {
            const apiurl = 'http://localhost:8000/api/liked_videos_api';

            fetch(apiurl, {
                method: 'GET',
                credentials: 'include',
            })
            .then(response => {
                if (response.status === 200) {
                    return response.json();
                } else {
                    throw new Error('Failed to fetch liked videos');
                }
            })
            .then(data => {
                setIsLoading(false);
                // setVideos(data);
            })
            .catch(error => {
                setIsLoading(false);
                console.error('Error fetching user info:', error);
            });
        }
        fetchData();



        const handleOnlineStatusChange = () => {
            setIsOnline(navigator.onLine);
            fetchData();
        };
        window.addEventListener('online', handleOnlineStatusChange);
        window.addEventListener('offline', handleOnlineStatusChange);
        return () => {
            window.removeEventListener('online', handleOnlineStatusChange);
            window.removeEventListener('offline', handleOnlineStatusChange);
        };
        





    }, [])


    return (
        <>
            <Helmet>
                <title>Library - YouTube</title>
            </Helmet>

            <LoadingBar
                color="#ff0000" // Customize the color (e.g., red)
                height={1.5}       // Customize the height (4 pixels)
                progress={isLoading ? 30 : 100} // Set progress based on loading state
            />

            <Navbar userInfo={userInfo} />
            <div className="main">
                <Side />
                <Sidebar />
                <div className="main-scroll">
                    {isOnline ? (
                        <>
                            {/* <h2 style={{padding: '20px 20px 0px 20px'}}>Library</h2> */}
                            <div className="inner">
                                {isLoading ? (
                                    <label>Loading...</label>
                                ):(
                                    <>
                                        <div className="subcon">
                                            <div class="heading" style={{width: '100%', display: 'flex', alignItems: 'center', justifyContent: 'space-between'}}>
                                                <label style={{width: 'auto', marginBottom: '15px'}}>
                                                    <svg viewBox="0 0 24 24" preserveAspectRatio="xMidYMid meet" focusable="false" class="style-scope yt-icon"><g class="style-scope yt-icon"><path d="M14.97,16.95L10,13.87V7h2v5.76l4.03,2.49L14.97,16.95z M22,12c0,5.51-4.49,10-10,10S2,17.51,2,12h1c0,4.96,4.04,9,9,9 s9-4.04,9-9s-4.04-9-9-9C8.81,3,5.92,4.64,4.28,7.38C4.17,7.56,4.06,7.75,3.97,7.94C3.96,7.96,3.95,7.98,3.94,8H8v1H1.96V3h1v4.74 C3,7.65,3.03,7.57,3.07,7.49C3.18,7.27,3.3,7.07,3.42,6.86C5.22,3.86,8.51,2,12,2C17.51,2,22,6.49,22,12z" class="style-scope yt-icon"></path></g></svg>
                                                    History
                                                </label>
                                            </div>
                                            div.su
                                        </div>
                                    </>
                                )}
                            </div>
                        </>
                    ):(
                        <NoInternetPage />
                    )}
                
                    
                </div>
            </div>
        </>
    )
}
export default Library;
