import { useEffect, useState } from "react";
import { Helmet } from "react-helmet";
import { useParams } from "react-router-dom";
import LoadingBar from "react-top-loading-bar";
import Navbar from "../Navbar";
import Side from "../Side";
import Sidebar from "../Sidebar";
import Channel from "./Channel";

export default function ChannelPlaylist({ userInfo, subdata }){
    const [videos, setVideos] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    
    const { channelslug } = useParams();
    const channelslu = channelslug.replace('@', '');

    useEffect(() => {
        const videos_data = () => {
            fetch('http://localhost:8000/api/user_videos', {
                method: 'GET',
                credentials: 'include',
            })
            .then(response => {
                if(response.status === 200){
                    return response.json();
                } else {
                    throw new Error('failed to fetch user videos..');
                }
            })
            .then(data => {
                setVideos(data.videos);
                setIsLoading(false);
            })
            .catch(error => {
                console.error('Error fetching user info:', error);
            });
        }
        videos_data();
    }, [])


    return (
        <>
            <Helmet>
                {userInfo.channeluser ? (
                    <title>{`${userInfo.channeluser} - YouTube`}</title>
                ):(
                    <title>YouTube</title>
                )}
            </Helmet>
            <LoadingBar
                color="#ff0000" // Customize the color (e.g., red)
                height={1.5}       // Customize the height (4 pixels)
                progress={isLoading ? 30 : 100} // Set progress based on loading state
            />
            <Navbar userInfo={userInfo} />
            <div className="main">
                <Side />
                <Sidebar subdata={subdata} />
                <div className="main-scroll">
                    <div className="channellayer">
                        <Channel channelslug={channelslu} />
                    </div>
                </div>
            </div>
        </>
    )
}
