import { useParams } from "react-router-dom";
import Navbar from "../Navbar";
import Side from "../Side";
import Sidebar from "../Sidebar";
import { useEffect, useState } from "react";
import AuthorChannelLayer from "./AuthorChannelLayer";
import AuthorChannelButtons from "./AuthorChannelButtons";
import AuthorChannelHome from "./AuthorChannelHome";

function AuthorChannelVideos({ userInfo }){
    const [authorinfo, setAuthorInfo] = useState([]);
    const { authorslug } = useParams();

    useEffect(() => {
        fetch(`http://localhost:8000/api/${authorslug}`, {
          method: 'GET',
          credentials: 'include',
        })
        .then(response => {
          if (response.status === 200) {
              return response.json();
          } else {
              throw new Error('Failed to fetch user info');
          }
        })
        .then(data => {
            setAuthorInfo(data.author_data[0]);
            // console.log(data.author_data[0])
        })
    }, [])

    return (
        <>
            <AuthorChannelHome userInfo={userInfo} />
            <div className="channel-inner">
                videos
            </div>
        </>
    )
}
export default AuthorChannelVideos;
