import './App.css';
import './assets/script.js';
import Home from './pages/Home';
import Explore from './pages/Explore';
import {Routes, Route} from 'react-router-dom';
import Subscriptions from './pages/Subscriptions';
import Library from './pages/Library';
import VideoDetail from './pages/VideoDetail';
import HiddenSideBar from './components/HiddenSideBar';
import Search from './pages/Search';
import History from './pages/History';
import ChannelHome from './components/channel/ChannelHome';
import Channel from './components/channel/Channel';
import ChannelVideos from './components/channel/ChannelVideos';
import ChannelAbout from './components/channel/ChannelAbout';
import { useState } from 'react';
import axios from 'axios';


function App() {
  const [userInfo, setUserInfo] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  

  const fetchUserData = () => {
    const apiurl = 'http://localhost:8000/api/get_user_data';

    fetch(apiurl, {
        method: 'GET',
        credentials: 'include',
    })
    .then(response => {
        if (response.status === 200) {
            return response.json();
        } else {
            throw new Error('Failed to fetch user info');
        }
    })
    .then(data => {
        setUserInfo(data.user);
    })
    .catch(error => {
        console.error('Error fetching user info:', error);
    });
}
fetchUserData();


const fetchData = () => {
  const apiurl = 'http://localhost:8000/api/explore';

  axios.get(apiurl)
  .then(response => {
      setIsLoading(false);
      setVideos(response.data);
      setTimeout(() => {
          setDelayedLoading(true);
      }, 1000);
  })
  .catch(error => {
      console.log('error fetching videos', error);
      setIsLoading(false);
  })
} 
fetchData();

  return (
    <>
    <HiddenSideBar />
      <Routes>
        <Route exact path='/' element={<Home />}></Route>
        <Route exact path='/watch/:videoId' element={<VideoDetail userInfo={userInfo} />}></Route>
        <Route exact path='/feed/explore' element={<Explore />}></Route>
        <Route exact path='/feed/subscriptions' element={<Subscriptions />}></Route>
        <Route exact path='/feed/library' element={<Library />}></Route>
        <Route exact path='/feed/history' element={<History />}></Route>
        <Route exact path='/search' element={<Search />}></Route>
        
        <Route exact path='/channel/:channelId' element={<ChannelHome />}></Route>
        <Route exact path='/channel/:channelId/videos' element={<ChannelVideos />}></Route>
        <Route exact path='/channel/:channelId/about' element={<ChannelAbout />}></Route>
        
      </Routes>
    </>
  );
}

export default App;
