import { useEffect, useState } from 'react';
import logo from '../../assets/author.png';
import { NavLink, useNavigate } from 'react-router-dom';
import { useLocation } from 'react-router-dom';

export default function Channel({ channelslug, onDataReceived }){
    const [userInfo, setUserInfo] = useState([]);
    const location = useLocation();
    const navigate = useNavigate();

    const isHomePage = location.pathname === `/c/@${channelslug}`;
    const isVideoPage = location.pathname === `/c/@${channelslug}/videos`;
    const isChannelPage = location.pathname === `/c/@${channelslug}/channels`;
    const isAboutPage = location.pathname === `/c/@${channelslug}/about`;

    const mousewheel = () => {
        var container = document.querySelector('.sticky-buttons .links');
        if(container){
            container.addEventListener('wheel', function(e){
                e.preventDefault();
                container.scrollLeft += e.deltaY;
            })
        }
    }
    mousewheel();

    useEffect(() => {
        fetch(`http://localhost:8000/api/user_channel_json/${channelslug}`, {
            method: 'GET',
            credentials: 'include',
        })
        .then(response => {
            if (response.status === 200) {
                return response.json();
            } else {
                navigate('/');
                throw new Error('Failed to fetch user info');
            }
        })
        .then(data => {
            setUserInfo(data);
            onDataReceived(data);
        })
        .catch(error => {
            console.error('Error fetching user info:', error);
        });
    }, [channelslug, navigate])

    return (
        <>
                


                <Navbar userInfo={userInfo} />
            <div className="main">
                <Side />
                <Sidebar subdata={subdata} />
                <div className="main-scroll">
                    <div className="channellayer">
                        <Channel channelslug={channelslu} onDataReceived={onDataReceivedFromChild} />
                        <div className="channelvideoscon">
                            <h4>Videos ({videos.length})</h4>
                            <div className="channelvideoscon-inner">
                                {videos.length <= 4 ? (
                                    <>
                                        {videos.map((video) => (
                                            <div className="channelvid" key={video.id}>
                                                <div className="channelvid-img">
                                                    <label>{video.duration}</label>
                                                    <NavLink to='/'>
                                                        <img alt='s' src={delayedLoading ? video.image : thumbnail} />
                                                    </NavLink>
                                                </div>
                                                <div className="channelvid-detail">
                                                    <div className="meta">
                                                        <h3>{video.title}</h3>
                                                        <div className="metablock">
                                                            <span>{video.views} views</span>&bull;
                                                            <span>{video.published}</span>
                                                        </div>
                                                    </div>
                                                    <div className="menu">
                                                        <button>
                                                            <svg height="24" viewBox="0 0 24 24" width="24" focusable="false"><path d="M12 16.5c.83 0 1.5.67 1.5 1.5s-.67 1.5-1.5 1.5-1.5-.67-1.5-1.5.67-1.5 1.5-1.5zM10.5 12c0 .83.67 1.5 1.5 1.5s1.5-.67 1.5-1.5-.67-1.5-1.5-1.5-1.5.67-1.5 1.5zm0-6c0 .83.67 1.5 1.5 1.5s1.5-.67 1.5-1.5-.67-1.5-1.5-1.5-1.5.67-1.5 1.5z"></path></svg>
                                                        </button>
                                                    </div>
                                                </div>
                                            </div>
                                        ))}
                                        <div className="channelvid"></div>
                                        <div className="channelvid"></div>
                                        <div className="channelvid"></div>
                                        <div className="channelvid"></div>
                                        <div className="channelvid"></div>
                                        <div className="channelvid"></div>
                                    </>
                                ):(
                                    <>
                                        {videos.map((video) => (
                                            <div className="channelvid" key={video.id}>
                                                <div className="channelvid-img">
                                                    <label>{video.duration}</label>
                                                    <NavLink to='/'>
                                                        <img alt='s' src={delayedLoading ? video.image : thumbnail} />
                                                    </NavLink>
                                                </div>
                                                <div className="channelvid-detail">
                                                    <div className="meta">
                                                        <h3>{video.title}</h3>
                                                        <div className="metablock">
                                                            <span>{video.views} views</span>&bull;
                                                            <span>{video.published}</span>
                                                        </div>
                                                    </div>
                                                    <div className="menu">
                                                        <button>
                                                            <svg height="24" viewBox="0 0 24 24" width="24" focusable="false"><path d="M12 16.5c.83 0 1.5.67 1.5 1.5s-.67 1.5-1.5 1.5-1.5-.67-1.5-1.5.67-1.5 1.5-1.5zM10.5 12c0 .83.67 1.5 1.5 1.5s1.5-.67 1.5-1.5-.67-1.5-1.5-1.5-1.5.67-1.5 1.5zm0-6c0 .83.67 1.5 1.5 1.5s1.5-.67 1.5-1.5-.67-1.5-1.5-1.5-1.5.67-1.5 1.5z"></path></svg>
                                                        </button>
                                                    </div>
                                                </div>
                                            </div>
                                        ))}
                                    </>
                                )}
                                
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}
