import React, { useEffect, useRef, useState } from 'react';

const LazyImage = ({ src, 'data-real-src': realSrc, alt }) => {
  const imgRef = useRef(null);
  const [shouldLoad, setShouldLoad] = useState(false);
  const randomDelay = Math.floor(Math.random() * 2000) + 1000; // Random delay between 1 to 3 seconds (1000ms = 1s)

  useEffect(() => {
    const observer = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          setTimeout(() => {
            setShouldLoad(true); // Trigger the image load after the random delay
          }, randomDelay);
          observer.unobserve(imgRef.current);
        }
      });
    });

    observer.observe(imgRef.current);

    return () => {
      observer.unobserve(imgRef.current);
    };
  }, [randomDelay]);

  return <img ref={imgRef} src={shouldLoad ? realSrc : src} alt={alt} />;
};

export default LazyImage;
