import { useParams } from "react-router-dom";
import Navbar from "../Navbar";
import Side from "../Side";
import Sidebar from "../Sidebar";
import { useEffect, useState } from "react";
import AuthorChannelLayer from "./AuthorChannelLayer";
import AuthorChannelButtons from "./AuthorChannelButtons";

function AuthorChannelAbout({ userInfo }){
    const [authorinfo, setAuthorInfo] = useState([]);
    const { authorslug } = useParams();

    useEffect(() => {
        fetch(`http://localhost:8000/api/${authorslug}`, {
              method: 'GET',
              credentials: 'include',
            })
            .then(response => {
              if (response.status === 200) {
                  return response.json();
              } else {
                  throw new Error('Failed to fetch user info');
              }
            })
            .then(data => {
                setAuthorInfo(data.author_data[0]);
            })
    }, [authorslug])

    return (
        <>
            <Navbar userInfo={userInfo} />
            <div className="main">
                <Side />
                <Sidebar />
                <div className="main-scroll">
                    <div className="channelcon">
                        <div className="banner" style={{backgroundImage: `url(${authorinfo.channelbanner})`}}>

                        </div>
                        <div className="channel-outer">
                            <AuthorChannelLayer authorinfo={authorinfo} />
                            <AuthorChannelButtons authorslug={authorslug} />
                            <div className="aboutdesc">
                                <div className="left">
                                    <div className="descript">
                                        <label>Description</label>
                                        <p></p>
                                            <p>{userInfo.description}</p>
                                        <p></p>
                                    </div>
                                </div>
                                    <div className="right">
                                        <div className="stats">
                                            <label>Stats</label>
                                            <div className="line"></div>
                                            <span>Joined {userInfo.joined}</span>
                                            <div className="line"></div>
                                            <span>{totalview} views</span>
                                        </div>
                                    </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}
export default AuthorChannelAbout;
