import { Helmet } from "react-helmet";
import Navbar from "../components/Navbar";
import Side from "../components/Side";
import Sidebar from "../components/Sidebar";
import { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import LoadingBar from "react-top-loading-bar";

function Search(){
    const location = useLocation();
    const queryParams = new URLSearchParams(location.search);
    const query = queryParams.get('query');
    const [results, setResults] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {

        function searchData(){
            fetch(`http://localhost:8000/api/search?query=${query}`, {
                method: 'GET',
                credentials: 'include'
            })
            .then((response) => response.json())
            .then((data) => {
                setResults(data);
                console.log(data);
                setIsLoading(false);
            })
            .catch((error) => console.error('Error searching:', error));
        }
        searchData();
        
    }, [query])
    return (
        <>
            <Helmet>
                <title>Search - YouTube</title>
            </Helmet>
            <LoadingBar
                color="#ff0000" // Customize the color (e.g., red)
                height={1.5}       // Customize the height (4 pixels)
                progress={isLoading ? 30 : 100} // Set progress based on loading state
            />
            <Navbar />
            <div className="main">
                <Side />
                <Sidebar />
                <div className="main-scroll">
                    <h2 style={{paddi}}>Search results for: {query}</h2>
                    <div className="inner">
                        <div className="explorecon">

                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}

export default Search;
