import { useParams } from "react-router-dom";
import Navbar from "../Navbar";
import Side from "../Side";
import Sidebar from "../Sidebar";
import { useEffect, useState } from "react";

function AuthorChannelHome({ userInfo }){
    const [authorinfo, setAuthorInfo] = useState([]);
    const { authorslug } = useParams();

    useEffect(() => {
        fetch(`http://localhost:8000/api/${authorslug}`, {
          method: 'GET',
          credentials: 'include',
        })
        .then(response => {
          if (response.status === 200) {
              return response.json();
          } else {
              throw new Error('Failed to fetch user info');
          }
        })
        .then(data => {
            setAuthorInfo(data.author_data[0]);
            // console.log(data.author_data[0])
        })
    }, [])

    return (
        <>
            <Navbar userInfo={userInfo} />
            <div className="main">
                <Side />
                <Sidebar />
                <div className="main-scroll">
                    <div className="channelcon">
                        <div className="banner" style={{backgroundImage: `url(${authorinfo.channelbanner})`}}>
                            
                        </div>
                        <div className="channel-outer">
                            {authorinfo.channelnam}
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}
export default AuthorChannelHome;
