import { useState, useEffect } from "react";
import thumbnail from '../../assets/placeholder.jpg'
import { useParams } from "react-router-dom";
import { Helmet } from "react-helmet";
import { NavLink } from "react-router-dom";
import ChannelLayer from "./ChannelLayer";
import ChannelButtons from "./ChannelButtons";

function ChannelHome(){
    const [delayedLoading, setDelayedLoading] = useState(false);
    const [title, setTitle] = useState([]);
    const { channelId } = useParams();

    useEffect(() => {
        
        const videos_data = () => {
            fetch('http://localhost:8000/api/user_videos', {
                method: 'GET',
                credentials: 'include',
            })
            .then(response => {
                if(response.status == 200){
                    return response.json();
                } else {
                    throw new Error('failed to fetch user videos..');
                }
            })
            .then(data => {
                setTitle(data.channeluser);
                // setIsLoading(false);
                setTimeout(() => {
                    setDelayedLoading(true);
                }, 100);
            })
            .catch(error => {
                console.error('Error fetching user info:', error);
            });
        }
        videos_data();


        


    }, []);



    return (
        <>
            <Helmet>
                <title>{`${title} - YouTube`}</title>
            </Helmet>

            <Navbar />
            <div className="main">
                <Side />
                <Sidebar />
                <div className="main-scroll">
                    <div className="channelcon">
                        <div className="banner" style={{}}>
                            
                        </div>
                        <div className="channel-outer">
                            <ChannelLayer />
                            <ChannelButtons />


                        </div>
                    </div>
                </div>
            </div>




            
        </>
    )
}
export default ChannelHome;
