import { useState, useEffect } from "react";
import thumbnail from '../../assets/placeholder.jpg'
import { useParams, useLocation } from "react-router-dom";
import { Helmet } from "react-helmet";
import { NavLink } from "react-router-dom";
import ChannelLayer from "./ChannelLayer";
import ChannelButtons from "./ChannelButtons";
import Navbar from "../Navbar";
import Side from "../Side";
import Sidebar from "../Sidebar";

function ChannelHome(){
    const [length, setLength] = useState(0);
    const [videos, setVideos] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [delayedLoading, setDelayedLoading] = useState(false);
    const [userInfo, setUserInfo] = useState([]);
    const location = useLocation();
    
    const { channelId } = useParams();

    useEffect(() => {
        const apiurl = 'http://localhost:8000/api/get_user_data';
        fetch(apiurl, {
            method: 'GET',
            credentials: 'include',
        })
        .then(response => {
            if (response.status === 200) {
                return response.json();
            } else {
                throw new Error('Failed to fetch user info');
            }
        })
        .then(data => {
            setUserInfo(data.user);
            
        })
        .catch(error => {
            console.error('Error fetching user info:', error);
        });
        



        const videos_data = () => {
            fetch('http://localhost:8000/api/user_videos', {
                method: 'GET',
                credentials: 'include',
            })
            .then(response => {
                if(response.status == 200){
                    return response.json();
                } else {
                    throw new Error('failed to fetch user videos..');
                }
            })
            .then(data => {
                setLength(data.videos_count);
                setVideos(data.videos);
                setIsLoading(false);
                setTimeout(() => {
                    setDelayedLoading(true);
                }, 100);
            })
            .catch(error => {
                console.error('Error fetching user info:', error);
            });
        }
        videos_data();


    }, []);


    return (
        <>
            <Helmet>
                <title>{`${userInfo.channeluser} - YouTube`}</title>
            </Helmet>

            <Navbar />
            <div className="main">
                <Side />
                <Sidebar />
                <div className="main-scroll">
                    <div className="channelcon">
                        <div className="banner" style={{backgroundImage: `url(${userInfo.channelbanner})`}}>
                            
                        </div>
                        <div className="channel-outer">
                            <ChannelLayer userInfo={userInfo} channelId={channelId} />
                            <ChannelButtons channelId={channelId} />


                            <div className="channel-inner">
                                <div className="featured">
                                    <NavLink to='/'>
                                        <div className="f-inner">
                                            <div onClick={() => {'/'}}>
                                                <div className="feature-img">
                                                    <video src="" autoPlay loop muted controls></video>
                                                </div>
                                            </div>
                                            <div className="feature-detail">
                                                <div onClick={() => {'/'}}>
                                                    <h4>title</h4>
                                                </div>
                                                <div onClick={() => {'/'}}>author</div>
                                                <span>
                                                    <span>23 views</span> • <span>2 days ago</span>
                                                </span>  
                                            </div>
                                        </div>
                                    </NavLink>
                                </div>

                                <div className="posts">
                                    <p>Videos ({length})</p>
                                    <div className="post-inner">
                                        { isLoading ? (
                                            <label>Loading...</label>
                                        ):(
                                            <>
                                                {videos.map((video) = (
                                                    <div className="post">
                                                    <NavLink to='/'>
                                                        <div className="post-img">
                                                            <img src={thumbnail} data-real-src={thumbnail} />   
                                                            <label>00:08</label>      
                                                        </div>
                                                    </NavLink>
                                                    <div className="post-detail">
                                                        <NavLink>
                                                            <h3>title</h3>
                                                        </NavLink>
                                                        <NavLink to='/'>marvel</NavLink>
                                                        <span>
                                                            <span>34 views</span> • <span>2 days ago</span>
                                                        </span>
                                                    </div>   
                                                </div> 
                                                ))}
                                                
                                            </>
                                        )}
                                         
                                        <div className="post"></div>  
                                        <div className="post"></div>  
                                        <div className="post"></div>  
                                        <div className="post"></div>  
                                        <div className="post"></div>  
                                        <div className="post"></div>  
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}
export default ChannelHome;
