import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Helmet } from 'react-helmet';
import Navbar from '../components/Navbar';
import { useParams } from 'react-router-dom';
import { NavLink } from 'react-router-dom';
import LoadingBar from 'react-top-loading-bar';


const VideoDetail = () => {
    const [video, setVideo] = useState({});
    const [videos, setVideos] = useState([]);
    const [delayedLoading, setDelayedLoading] = useState(false);
    const [isLoading, setIsLoading] = useState(true);
    
    const { videoId } = useParams();

    const fetchData = () => {
        const apiurl = 'http://localhost:8000/api/explore';
      
        axios.get(apiurl)
        .then(response => {
            setIsLoading(false);
            setVideos(response.data);
            setTimeout(() => {
                setDelayedLoading(true);
            }, 1000);
        })
        .catch(error => {
            console.log('error fetching videos', error);
            setIsLoading(false);
        })
    } 
    fetchData();


    const fetchUserData = () => {
        const apiurl = 'http://localhost:8000/api/get_user_data';
    
        fetch(apiurl, {
            method: 'GET',
            credentials: 'include',
        })
        .then(response => {
            if (response.status === 200) {
                return response.json();
            } else {
                throw new Error('Failed to fetch user info');
            }
        })
        .then(data => {
            setUserInfo(data.user);
        })
        .catch(error => {
            console.error('Error fetching user info:', error);
        });
    }
    fetchUserData();
    


    function handleOpen(){
        var descinner = document.querySelector('.descinner');
        var showmore = document.querySelector('.descinner button');

        descinner.classList.toggle('active');
        if(showmore.innerHTML == 'more'){
            showmore.innerHTML = 'less';
        } else {
            showmore.innerHTML = 'more';
        }
    }
    function openPostingCon(){
        var postingcon = document.querySelector('.posting');
        var anchorposting = document.querySelector('.posting a');
        var commentinput = document.querySelector('.commentcon form input');

        postingcon.style.display = 'flex';
    }
    function closePostingCon(){
        var postingcon = document.querySelector('.posting');
        var anchorposting = document.querySelector('.posting a');
        var commentinput = document.querySelector('.commentcon form input');

        postingcon.style.display = 'none';
    }

    useEffect(() => {
        const videoid = videoId;
        const apiurl = `http://localhost:8000/api/get_video/${videoid}`;

        axios.get(apiurl)
        .then(response => {
            console.log(response.data);
            document.title = `${response.data.title} - YouTube`;
            setIsLoading(false);
            setVideo(response.data);
             // Set loading to false when data is received
        })
        .catch(error => {
            console.error('Error fetching video details:', error);
            setIsLoading(false); // Set loading to false on error
        });
    }, [videoId]);


    return (
        <>
            <Helmet>
                <title>Video Detail - YouTube</title>
            </Helmet>

            <LoadingBar
                color="#ff0000" // Customize the color (e.g., red)
                height={1.5}       // Customize the height (4 pixels)
                progress={isLoading ? 30 : 100} // Set progress based on loading state
            />


            <Navbar />
            <div className="main">
                <div className="main-scroll" style={{width: '100%'}}>
                    <div className='inner'>
                        <div class="video-container">
                            <div class="left">
                                <div class="videocon">
                                    <video src={video.video} autoplay loop muted controls></video>
                                </div>
                                <div class="metadata">
                                    <div class="abovefold">
                                        <div class="title">
                                            <h1>{video.title}</h1>
                                        </div>
                                        <div class="toprow">
                                            <div class="owner">
                                                <div class="ownerleft">
                                                    <NavLink to='/'>
                                                        <div class="ownerimg">
                                                            <img alt='s' src={video.authorimg} />
                                                        </div>
                                                    </NavLink>
                                                    <div class="uploadinfo">
                                                        <div class="channelname">
                                                            <NavLink to='/'>
                                                                {video.author}
                                                            </NavLink>
                                                            
                                                        </div>
                                                        <label>{video.authorsubs} subscribers</label>
                                                    </div>
                                                    
                                                </div>
                                                <div class="ownerright">
                                                    <div class="subscribe">
                                                        <button class="subscribe">Subscribe</button>
                                                        {/* <button class="subscribed">
                                                            <svg enable-background="new 0 0 24 24" height="24" viewBox="0 0 24 24" width="24" focusable="false"><path d="M10 20h4c0 1.1-.9 2-2 2s-2-.9-2-2zm10-2.65V19H4v-1.65l2-1.88v-5.15C6 7.4 7.56 5.1 10 4.34v-.38c0-1.42 1.49-2.5 2.99-1.76.65.32 1.01 1.03 1.01 1.76v.39c2.44.75 4 3.06 4 5.98v5.15l2 1.87zm-1 .42-2-1.88v-5.47c0-2.47-1.19-4.36-3.13-5.1-1.26-.53-2.64-.5-3.84.03C8.15 6.11 7 7.99 7 10.42v5.47l-2 1.88V18h14v-.23z"></path></svg>
                                                            Subscribed
                                                        </button> */}
                                                    </div>
                                                </div>
                                            </div>
                                            <div class="actions">
                                                <div class="actionsinner">
                                                    <div id="menu">
                                                        <div class="toplevel">
                                                            <div class="likedislike">
                                                                <div class="like">
                                                                    <button>
                                                                        <svg viewBox="0 0 24 24" preserveAspectRatio="xMidYMid meet" focusable="false" class="style-scope yt-icon"><g class="style-scope yt-icon"><path d="M18.77,11h-4.23l1.52-4.94C16.38,5.03,15.54,4,14.38,4c-0.58,0-1.14,0.24-1.52,0.65L7,11H3v10h4h1h9.43 c1.06,0,1.98-0.67,2.19-1.61l1.34-6C21.23,12.15,20.18,11,18.77,11z M7,20H4v-8h3V20z M19.98,13.17l-1.34,6 C18.54,19.65,18.03,20,17.43,20H8v-8.61l5.6-6.06C13.79,5.12,14.08,5,14.38,5c0.26,0,0.5,0.11,0.63,0.3 c0.07,0.1,0.15,0.26,0.09,0.47l-1.52,4.94L13.18,12h1.35h4.23c0.41,0,0.8,0.17,1.03,0.46C19.92,12.61,20.05,12.86,19.98,13.17z" class="style-scope yt-icon"></path></g></svg>
                                                                        <label>{video.liked}</label>
                                                                    </button>
                                                                </div>
                                                                <div class="dislike">
                                                                    <button>
                                                                        <svg height="24" viewBox="0 0 24 24" width="24" focusable="false"><path d="M17,4h-1H6.57C5.5,4,4.59,4.67,4.38,5.61l-1.34,6C2.77,12.85,3.82,14,5.23,14h4.23l-1.52,4.94C7.62,19.97,8.46,21,9.62,21 c0.58,0,1.14-0.24,1.52-0.65L17,14h4V4H17z M10.4,19.67C10.21,19.88,9.92,20,9.62,20c-0.26,0-0.5-0.11-0.63-0.3 c-0.07-0.1-0.15-0.26-0.09-0.47l1.52-4.94l0.4-1.29H9.46H5.23c-0.41,0-0.8-0.17-1.03-0.46c-0.12-0.15-0.25-0.4-0.18-0.72l1.34-6 C5.46,5.35,5.97,5,6.57,5H16v8.61L10.4,19.67z M20,13h-3V5h3V13z"></path></svg>
                                                                    </button>
                                                                </div>
                                                            </div>
                                                            <div class="likedislike share">
                                                                <div class="like">
                                                                    <button>
                                                                        <svg height="24" viewBox="0 0 24 24" width="24" focusable="false"><path d="M15 5.63 20.66 12 15 18.37V14h-1c-3.96 0-7.14 1-9.75 3.09 1.84-4.07 5.11-6.4 9.89-7.1l.86-.13V5.63M14 3v6C6.22 10.13 3.11 15.33 2 21c2.78-3.97 6.44-6 12-6v6l8-9-8-9z"></path></svg>
                                                                        <label>Share</label>
                                                                    </button>
                                                                </div>
                                                            </div>
                                                            

                                                        </div>
                                                        <div class="flexible-item one">
                                                            <div class="likedislike download">
                                                                <div class="like">
                                                                    <button>
                                                                        <svg height="24" viewBox="0 0 24 24" width="24" focusable="false"><path d="M17 18v1H6v-1h11zm-.5-6.6-.7-.7-3.8 3.7V4h-1v10.4l-3.8-3.8-.7.7 5 5 5-4.9z"></path></svg>
                                                                        <label>Download</label>
                                                                    </button>
                                                                </div>
                                                            </div>
                                                        </div>
                                                        <div class="flexible-item two">
                                                            <div class="likedislike options">
                                                                <div class="like">
                                                                    <button>
                                                                        <svg height="24" viewBox="0 0 24 24" width="24" focusable="false"><path d="M7.5 12c0 .83-.67 1.5-1.5 1.5s-1.5-.67-1.5-1.5.67-1.5 1.5-1.5 1.5.67 1.5 1.5zm4.5-1.5c-.83 0-1.5.67-1.5 1.5s.67 1.5 1.5 1.5 1.5-.67 1.5-1.5-.67-1.5-1.5-1.5zm6 0c-.83 0-1.5.67-1.5 1.5s.67 1.5 1.5 1.5 1.5-.67 1.5-1.5-.67-1.5-1.5-1.5z"></path></svg>
                                                                    </button>
                                                                </div>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                        <div class="bottomrow">
                                            <div class="description">
                                                <div class="descinner" onClick={handleOpen}>
                                                    <div class="info-container">
                                                        <div class="info">
                                                            <span>{video.views} views &bull;</span>
                                                            <span>{video.published}</span>
                                                        </div>
                                                    </div>
                                                    <div class="description-inline">
                                                        <p>{video.description}</p>
                                                    </div>
                                                    <button>more</button>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                                <div class="header">
                                    <div class="headertitle">
                                        <h2>
                                            <span>{video.commentscount}</span> <span>comments</span>
                                        </h2>
                                    </div>
                                    <div class="simplebox">
                                        <div class="simpleboxinner">
                                            <div class="commentbox">
                                                <div class="inputrow">
                                                    <div class="imgshow">
                                                        <img alt='s' src={userInfo.channelimage} />
                                                    </div>
                                                    <div class="commentcon">
                                                        <form action="#">
                                                            <input type="text" placeholder="Add a comment" onFocus={openPostingCon} />
                                                            <span></span>
                                                            <div class="posting">
                                                                <NavLink onClick={closePostingCon}>Cancel</NavLink>
                                                                <button type="submit" class="comment" disabled>Comment</button>
                                                            </div>
                                                        </form>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div class="right">
                                <div class="rightinner">
                                    <div class="items">
                                        {isLoading ? (
                                            <>
                                                <label>Loading...</label>
                                            </>
                                        ):(
                                            <>
                                                {videos.map(video => (
                                                    <>
                                                        <div class="item">
                                                            <div class="item-img">
                                                                <img alt='s' src={video.image} />
                                                            </div>
                                                            <div class="item-detail">
                                                                <div class="item-detail-inner">
                                                                    <h3>{video.title}</h3>
                                                                    <NavLink to='/'>{video.author}</NavLink>
                                                                    <span>
                                                                        <label>{video.views} views &nbsp;&bull;&nbsp;</label>
                                                                        <label>{video.published}</label>
                                                                    </span>
                                                                </div>
                                                            </div>
                                                        </div>
                                                    </>
                                                ))}
                                            </>
                                        )}
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}

export default VideoDetail;
