import { useState, useEffect } from "react";
import thumbnail from '../../assets/placeholder.jpg'
import { useParams } from "react-router-dom";
import { Helmet } from "react-helmet";
import { NavLink } from "react-router-dom";
import ChannelLayer from "./ChannelLayer";
import ChannelButtons from "./ChannelButtons";
import Navbar from "../Navbar";
import Side from "../Side";
import Sidebar from "../Sidebar";
import LoadingBar from "react-top-loading-bar";

function ChannelHome(){
    const [length, setLength] = useState(0);
    const [videos, setVideos] = useState([]);
    const [featured, setFeatured] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [delayedLoading, setDelayedLoading] = useState(false);
    const [userInfo, setUserInfo] = useState([]);
    
    const { channelId } = useParams();

    useEffect(() => {
        const apiurl = 'http://localhost:8000/api/get_user_data';
        fetch(apiurl, {
            method: 'GET',
            credentials: 'include',
        })
        .then(response => {
            if (response.status === 200) {
                return response.json();
            } else {
                throw new Error('Failed to fetch user info');
            }
        })
        .then(data => {
            setUserInfo(data.user);
            
        })
        .catch(error => {
            console.error('Error fetching user info:', error);
        });
        
        const videos_data = () => {
            fetch('http://localhost:8000/api/user_videos', {
                method: 'GET',
                credentials: 'include',
            })
            .then(response => {
                if(response.status == 200){
                    return response.json();
                } else {
                    throw new Error('failed to fetch user videos..');
                }
            })
            .then(data => {
                setLength(data.videos_count);
                setVideos(data.videos);
                setTotalview(data.videos_count);
                setIsLoading(false);
                setTimeout(() => {
                    setDelayedLoading(true);
                }, 100);
            })
            .catch(error => {
                console.error('Error fetching user info:', error);
            });
        }
        videos_data();



        fetch('http://localhost:8000/api/featured_video', {
                method: 'GET',
                credentials: 'include',
        })
        .then(response => {
            if(response.status == 200){
                return response.json();
            } else {
                throw new Error('failed to fetch featured videos..');
            }
        })
        .then(data => {
            setFeatured(data[0]);
        })
        .catch(error => {
            console.error('Error fetching user info:', error);
        });


    }, []);


    return (
        <>
            <Helmet>
                <title>{`${userInfo.channeluser} - YouTube`}</title>
            </Helmet>

            <LoadingBar
                color="#ff0000" // Customize the color (e.g., red)
                height={1.5}       // Customize the height (4 pixels)
                progress={isLoading ? 30 : 100} // Set progress based on loading state
            />

            <Navbar />
            <div className="main">
                <Side />
                <Sidebar />
                <div className="main-scroll">
                    <div className="channelcon">
                        <div className="banner" style={{backgroundImage: `url(${userInfo.channelbanner})`}}>
                            
                        </div>
                        <div className="channel-outer">
                            <ChannelLayer userInfo={userInfo} channelId={channelId} />
                            <ChannelButtons channelId={channelId} />

                            <div className="channel-inner">
                                <div className="featured" style={{paddingLeft: '20px', paddingRight: '20px'}}>
                                    <NavLink to='/'>
                                        <div className="f-inner">
                                            <div onClick={() => {'/'}}>
                                                <div className="feature-img">
                                                    <video src={featured.video} autoPlay loop muted controls style={{borderRadius: '12px'}}></video>
                                                </div>
                                            </div>
                                            <div className="feature-detail">
                                                <div onClick={() => {'/'}}>
                                                    <h4>{featured.title}</h4>
                                                </div>
                                                <div onClick={() => {'/'}} style={{margin: '5px 0'}}>{featured.author}</div>
                                                <span>
                                                    <span>{featured.views} views</span> • <span>{featured.published}</span>
                                                </span>  
                                            </div>
                                        </div>
                                    </NavLink>
                                </div>

                                <div className="posts">
                                    <p>Videos ({length})</p>
                                    <div className="post-inner">
                                        { isLoading ? (
                                            <label>Loading...</label>
                                        ):(
                                            <>
                                                {videos.map((video) => (
                                                    <div className="post" key={video.id}>
                                                        <NavLink to='/'>
                                                            <div className="post-img">
                                                                <img src={delayedLoading ? video.image : thumbnail} data-real-src={video.image} />   
                                                                <label>00:08</label>      
                                                            </div>
                                                        </NavLink>
                                                        <div className="post-detail">
                                                            <NavLink>
                                                                <h3>{video.title}</h3>
                                                            </NavLink>
                                                            <NavLink to='/'>{video.author}</NavLink>
                                                            <span>
                                                                <span>{video.views} views</span> • <span>{video.published}</span>
                                                            </span>
                                                        </div>   
                                                    </div> 
                                                ))}
                                                <div className="post"></div>  
                                                <div className="post"></div>  
                                                <div className="post"></div>  
                                                <div className="post"></div>  
                                                <div className="post"></div>  
                                                <div className="post"></div> 
                                            </>
                                        )}
                 
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}
export default ChannelHome;
