import { useState, useEffect } from "react";
import thumbnail from '../../assets/placeholder.jpg';
import { useParams } from "react-router-dom";
import { Helmet } from "react-helmet";
import { NavLink } from "react-router-dom";
import Navbar from "../Navbar";

function ChannelAbout(){
    const [length, setLength] = useState(0);
    const [videos, setVideos] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [userInfo, setUserInfo] = useState([]);
    const [delayedLoading, setDelayedLoading] = useState(false);
    const { channelId } = useParams();


    useEffect(() => {
        const apiurl = 'http://localhost:8000/api/get_user_data';
        fetch(apiurl, {
            method: 'GET',
            credentials: 'include',
        })
        .then(response => {
            if (response.status === 200) {
                return response.json();
            } else {
                throw new Error('Failed to fetch user info');
            }
        })
        .then(data => {
            setUserInfo(data.user);
            
        })
        .catch(error => {
            console.error('Error fetching user info:', error);
        });
        
        const videos_data = () => {
            fetch('http://localhost:8000/api/user_videos', {
                method: 'GET',
                credentials: 'include',
            })
            .then(response => {
                if(response.status == 200){
                    return response.json();
                } else {
                    throw new Error('failed to fetch user videos..');
                }
            })
            .then(data => {
                setLength(data.videos_count);
                setVideos(data.videos);
                setIsLoading(false);
                setTimeout(() => {
                    setDelayedLoading(true);
                }, 100);
            })
            .catch(error => {
                console.error('Error fetching user info:', error);
            });
        }
        videos_data();
    }, []);


    return (
        <>
            <Helmet>
                <title>{`${userInfo.channeluser} - YouTube`}</title>
            </Helmet>


            <Navbar />
            <div className="main">
                <Side />
                <Sidebar />
                <div className="main-scroll">
                    <div className="channelcon">
                        <div className="banner" style={{}}>
                            
                        </div>
                        <div className="channel-outer">
                            
                        </div>
                    </div>
                </div>
            </div>





            <div className="channel-inner">
                <div class="aboutdesc">
                    <div class="left">
                        <div class="descript">
                            <label>Description</label>
                            <p></p>
                                <p>{userInfo.description}</p>
                            <p></p>
                        </div>
                    </div>
                    <div class="right">
                        <div class="stats">
                            <label>Stats</label>
                            <div class="line"></div>
                            <span>Joined {userInfo.joined}</span>
                            <div class="line"></div>
                            <span>23 views</span>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}
export default ChannelAbout;
