import { useEffect, useState } from "react";
import { Helmet } from "react-helmet";
import { useParams } from "react-router-dom";
import LoadingBar from "react-top-loading-bar";
import Navbar from "../Navbar";
import Side from "../Side";
import Sidebar from "../Sidebar";
import Channel from "./Channel";
import { NavLink } from "react-router-dom";
import LazyImage from "../../pages/LazyImage";
import thumbnail from '../../assets/placeholder.jpg';

export default function ChannelPlaylist({ userInfo, subdata, firstplaylist, playlists, playlistvideos }){
    const [isLoading, setIsLoading] = useState(true);
    
    const { channelslug } = useParams();
    const channelslu = channelslug.replace('@', '');

    useEffect(() => {
        const videos_data = () => {
            fetch('http://localhost:8000/api/user_videos', {
                method: 'GET',
                credentials: 'include',
            })
            .then(response => {
                if(response.status === 200){
                    return response.json();
                } else {
                    throw new Error('failed to fetch user videos..');
                }
            })
            .then(data => {
                setIsLoading(false);
            })
            .catch(error => {
                console.error('Error fetching user info:', error);
            });
        }
        videos_data();
    }, [])


    return (
        <>
            <Helmet>
                {userInfo.channeluser ? (
                    <title>{`${userInfo.channeluser} - YouTube`}</title>
                ):(
                    <title>YouTube</title>
                )}
            </Helmet>
            <LoadingBar
                color="#ff0000" // Customize the color (e.g., red)
                height={1.5}       // Customize the height (4 pixels)
                progress={isLoading ? 30 : 100} // Set progress based on loading state
            />
            <Navbar userInfo={userInfo} />
            <div className="main">
                <Side />
                <Sidebar subdata={subdata} />
                <div className="main-scroll">
                    <div className="channellayer">
                        <Channel channelslug={channelslu} />
                        <div className="channelvideoscon">
                            <h4>Videos ({playlists.length})</h4>
                            <div className="channelvideoscon-inner">
                                {playlists.length <= 4 ? (
                                    <>
                                        {playlists.map((video) => (
                                            <div className="channelvid" key={video.id}>
                                                <div className="channelvid-img">
                                                    <label>00:08</label>
                                                    <NavLink to='/'>
                                                        <LazyImage alt={firstplaylist.id} src={thumbnail} data-real-src={`http://localhost:8000/media/${firstplaylist.image}`} />
                                                    </NavLink>
                                                </div>
                                                <div className="channelvid-detail">
                                                    <div className="meta">
                                                        <h3>{video.playlistname}</h3>
                                                        <div className="metablock">
                                                            
                                                                <NavLink to='/'>View full playlist</NavLink>
                                                            
                                                        </div>
                                                    </div>
                                                    <div className="menu">
                                                        <button>
                                                            <svg height="24" viewBox="0 0 24 24" width="24" focusable="false"><path d="M12 16.5c.83 0 1.5.67 1.5 1.5s-.67 1.5-1.5 1.5-1.5-.67-1.5-1.5.67-1.5 1.5-1.5zM10.5 12c0 .83.67 1.5 1.5 1.5s1.5-.67 1.5-1.5-.67-1.5-1.5-1.5-1.5.67-1.5 1.5zm0-6c0 .83.67 1.5 1.5 1.5s1.5-.67 1.5-1.5-.67-1.5-1.5-1.5-1.5.67-1.5 1.5z"></path></svg>
                                                        </button>
                                                    </div>
                                                </div>
                                            </div>
                                        ))}
                                        <div className="channelvid"></div>
                                        <div className="channelvid"></div>
                                        <div className="channelvid"></div>
                                        <div className="channelvid"></div>
                                        <div className="channelvid"></div>
                                        <div className="channelvid"></div>
                                    </>
                                ):(
                                    <>
                                        {playlists.map((video) => (
                                            <div className="channelvid" key={video.id}>
                                                <div className="channelvid-img">
                                                    <label>00:08</label>
                                                    <NavLink to='/'>
                                                        <LazyImage alt={video.id} src={thumbnail} data-real-src={firstplaylist.image} />
                                                    </NavLink>
                                                </div>
                                                <div className="channelvid-detail">
                                                    <div className="meta">
                                                        <h3>{video.playlistname}</h3>
                                                        <div className="metablock">
                                                            <span>98views</span>&bull;
                                                            <span>2 days ago</span>
                                                        </div>
                                                    </div>
                                                    <div className="menu">
                                                        <button>
                                                            <svg height="24" viewBox="0 0 24 24" width="24" focusable="false"><path d="M12 16.5c.83 0 1.5.67 1.5 1.5s-.67 1.5-1.5 1.5-1.5-.67-1.5-1.5.67-1.5 1.5-1.5zM10.5 12c0 .83.67 1.5 1.5 1.5s1.5-.67 1.5-1.5-.67-1.5-1.5-1.5-1.5.67-1.5 1.5zm0-6c0 .83.67 1.5 1.5 1.5s1.5-.67 1.5-1.5-.67-1.5-1.5-1.5-1.5.67-1.5 1.5z"></path></svg>
                                                        </button>
                                                    </div>
                                                </div>
                                            </div>
                                        ))}
                                    </>
                                )}
                                
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}
