import { Route, Routes, useParams } from "react-router-dom";
import Navbar from "../Navbar";
import Side from "../Side";
import Sidebar from "../Sidebar";
import { useEffect, useState } from "react";
import AuthorChannelLayer from "./AuthorChannelLayer";
import AuthorChannelButtons from "./AuthorChannelButtons";
import AuthorChannelHome from "./AuthorChannelHome";
import AuthorChannelVideos from "./AuthorChannelVideos";
import { Helmet } from "react-helmet";
import AuthorChannelAbout from "./AuthorChannelAbout";

function AuthorChannel({ userInfo }){
    const [authorinfo, setAuthorInfo] = useState([]);
    const { authorslug } = useParams();

    useEffect(() => {
        fetch(`http://localhost:8000/api/${authorslug}`, {
          method: 'GET',
          credentials: 'include',
        })
        .then(response => {
          if (response.status === 200) {
              return response.json();
          } else {
              throw new Error('Failed to fetch user info');
          }
        })
        .then(data => {
            setAuthorInfo(data.author_data[0]);
        })
    }, [authorslug])

    return (
        <>
            <Helmet>
                <title>{`${authorinfo.channelname} - YouTube`}</title>
            </Helmet>
            <Navbar userInfo={userInfo} />
            <div className="main">
                <Side />
                <Sidebar />
                <div className="main-scroll">
                    <div className="channelcon">
                        <div className="banner" style={{backgroundImage: `url(${authorinfo.channelbanner})`}}>
                            
                        </div>
                        <div className="channel-outer">
                            <AuthorChannelLayer authorinfo={authorinfo} />
                            <AuthorChannelButtons authorslug={authorslug} />
                            <AuthorChannelHome />
                            <div className="channel-inner">
                                <Routes>
                                        <Route exact to='/' element={<AuthorChannelHome authorinfo={authorinfo} authorslug={authorslug} />}></Route>
                                        <Route exact to='/videos' element={<AuthorChannelVideos authorinfo={authorinfo} authorslug={authorslug} />}></Route>
                                        <Route e to='/about' element={<AuthorChannelAbout authorinfo={authorinfo} authorslug={authorslug} />}></Route>
                                </Routes>
                            </div>
                            
                            
                            
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}
export default AuthorChannel;
